/***************************
 * Check and Radio buttons *
 ***************************/
/************
 * Switches *
 ************/
* {
  padding: 0;
  -GtkToolButton-icon-spacing: 4;
  -GtkTextView-error-underline-color: #c7162b;
  -GtkCheckButton-indicator-size: 16;
  -GtkCheckMenuItem-indicator-size: 16;
  -GtkScrolledWindow-scrollbar-spacing: 0;
  -GtkScrolledWindow-scrollbars-within-bevel: 1;
  -GtkToolItemGroup-expander-size: 11;
  -GtkExpander-expander-size: 16;
  -GtkTreeView-expander-size: 11;
  -GtkTreeView-horizontal-separator: 4;
  -GtkMenu-horizontal-padding: 0;
  -GtkMenu-vertical-padding: 0;
  -GtkWidget-link-color: #19B6EE;
  -GtkWidget-visited-link-color: #48c5f2;
  -GtkWidget-focus-padding: 2;
  -GtkWidget-focus-line-width: 1;
  -GtkWidget-text-handle-width: 28;
  -GtkWidget-text-handle-height: 28;
  -GtkDialog-button-spacing: 4;
  -GtkDialog-action-area-border: 0;
  -GtkStatusbar-shadow-type: none;
  outline-color: rgba(247, 247, 247, 0.3);
  outline-style: dashed;
  outline-offset: -3px;
  outline-width: 1px;
  outline-radius: 2px; }

/***************
 * Base States *
 ***************/
.background {
  color: #F7F7F7;
  background-color: #1F3D3D; }
  .background:backdrop {
    text-shadow: none;
    icon-shadow: none;
    color: #999999;
    background-color: #1F3D3D; }

/*
   These wildcard seems unavoidable, need to investigate.
   Wildcards are bad and troublesome, use them with care,
   or better, just don't.
   Everytime a wildcard is used a kitten dies, painfully.
*/
*:insensitive {
  -gtk-image-effect: dim; }

.gtkstyle-fallback {
  background-color: #1F3D3D;
  color: #F7F7F7; }
  .gtkstyle-fallback:prelight {
  background-color: #1F3D3D;
    color: #F7F7F7; }
  .gtkstyle-fallback:active {
  background-color: #1F3D3D;
    color: #F7F7F7; }
  .gtkstyle-fallback:insensitive {
  background-color: #1F3D3D;
    color: #969696; }
  .gtkstyle-fallback:selected {
  background-color: #1F3D3D;
    color: #FFFFFF; }

.view {
  color: #F7F7F7;
  background-color: #1F3D3D; }
  .view:backdrop {
    color: rgba(247, 247, 247, 0.8);
  background-color: #1F3D3D; }
  .view:selected {
    border-radius: 3px; }

.rubberband, GtkTreeView.view.rubberband, .content-view.rubberband {
  border: 1px solid #318d3e;
  background-color: #1F3D3D; }

.label.separator {
  color: #F7F7F7; }
  .label.separator:backdrop {
    color: #999999; }

.label:insensitive {
  color: #969696; }
  .label:insensitive:backdrop {
    color: #616161; }

.dim-label, .label.separator, .titlebar .subtitle,
.header-bar .subtitle {
  opacity: 0.55;
  text-shadow: none; }

GtkAssistant .sidebar {
  background-color: #1F3D3D;
  border-top: 1px solid #2b2b2b; }
  GtkAssistant .sidebar:dir(ltr) {
    border-right: 1px solid #2b2b2b; }
  GtkAssistant .sidebar:dir(rtl) {
    border-left: 1px solid #2b2b2b; }
  GtkAssistant .sidebar:backdrop {
  background-color: #1F3D3D;
    border-color: #333333; }

GtkAssistant.csd .sidebar {
  border-top-style: none; }

GtkAssistant .sidebar .label {
  padding: 6px 12px; }

GtkAssistant .sidebar .label.highlight {
  background-color: #1F3D3D; }

GtkTextView {
  background-color: #1F3D3D; }
  GtkTextView:backdrop {
  background-color: #1F3D3D; }

.grid-child {
  padding: 3px;
  border-radius: 3px; }
  .grid-child:selected {
    outline-offset: -2px; }

.popover.osd, .app-notification,
.app-notification.frame, .osd .scale-popup, .osd {
  color: #F7F7F7;
  border: none;
  background-color: #1F3D3D;
  background-clip: padding-box;
  outline-color: rgba(247, 247, 247, 0.3);
  box-shadow: none;
  text-shadow: 0 1px black;
  icon-shadow: 0 1px black; }
  .popover.osd:backdrop, .app-notification:backdrop, .osd .scale-popup:backdrop, .osd:backdrop {
    text-shadow: none; }

/*********************
 * Spinner Animation *
 *********************/
@keyframes spin {
  to {
    -gtk-icon-transform: rotate(1turn); } }

.spinner {
  background-image: none;
  background-color: #1F3D3D;
  opacity: 0;
  color: #19B6EE;
  -gtk-icon-source: -gtk-icontheme("process-working-symbolic"); }
  .spinner:active {
    opacity: 1;
    animation: spin 1s linear infinite; }
    .spinner:active:insensitive {
      opacity: 0.5; }
  .spinner:backdrop {
    color: #11b2eb; }

/****************
 * Text Entries *
 ****************/
.entry {
