  .entry:focus {
    background-color: transparent;
    background-image: linear-gradient(to bottom, #2c2c2c, #363636 3px, #3D3D3D 90%);
    box-shadow: inset 0 1px 1px 0px rgba(0, 0, 0, 0.05);
    border-color: #24672e; }
  .entry:insensitive {
    background-color: transparent;
