/* GTK NAMED COLORS
   ----------------
   use responsibly! */
/*
widget text/foreground color */
@define-color theme_fg_color #F7F7F7;
/*
text color for entries, views and content in general */
@define-color theme_text_color #F7F7F7;
/*
widget base background color */
@define-color theme_bg_color #353535;
/*
text widgets and the like base background color */
@define-color theme_base_color #3D3D3D;
/*
base background color of selections */
@define-color theme_selected_bg_color #3eb34f;
/*
text/foreground color of selections */
@define-color theme_selected_fg_color #FFFFFF;
/*
base background color of insensitive widgets */
@define-color insensitive_bg_color #383838;
/*
text foreground color of insensitive widgets */
@define-color insensitive_fg_color #969696;
/*
insensitive text widgets and the like base background color */
@define-color insensitive_base_color #3D3D3D;
/*
widget text/foreground color on backdrop windows */
@define-color theme_unfocused_fg_color #999999;
/*
text color for entries, views and content in general on backdrop windows */
@define-color theme_unfocused_text_color #F7F7F7;
/*
widget base background color on backdrop windows */
@define-color theme_unfocused_bg_color #3a3a3a;
/*
text widgets and the like base background color on backdrop windows */
@define-color theme_unfocused_base_color #3D3D3D;
/*
base background color of selections on backdrop windows */
@define-color theme_unfocused_selected_bg_color #3eb34f;
/*
text/foreground color of selections on backdrop windows */
@define-color theme_unfocused_selected_fg_color #FFFFFF;
/*
widgets main borders color */
@define-color borders #2b2b2b;
/*
widgets main borders color on backdrop windows */
@define-color unfocused_borders #333333;
/*
these are pretty self explicative */
@define-color warning_color #f99b11;
@define-color error_color #c7162b;
@define-color success_color #0e8420;
/*
these colors are exported for the window manager and shouldn't be used in applications,
read if you used those and something break with a version upgrade you're on your own... */
