@define-color wm_button_active_color_a shade(#353535, 0.85);
@define-color wm_button_active_color_b shade(#353535, 0.89);
@define-color wm_button_active_color_c shade(#353535, 0.9);
@define-color content_view_bg #3D3D3D;

